import React, { useState, useMemo } from "react";
import papers from "../data/papers.json";

export default function Papers() {
  const [query, setQuery] = useState("");

  const filteredPapers = useMemo(() => {
    const sorted = [...papers].sort((a, b) => new Date(b.date) - new Date(a.date));
    if (!query) return sorted.slice(0, 10);
    return sorted.filter(p =>
      p.title.toLowerCase().includes(query.toLowerCase()) ||
      p.authors.toLowerCase().includes(query.toLowerCase())
    );
  }, [query]);

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold text-[#cc0033] mb-6 text-center">Research Papers</h1>

      <div className="mb-8 text-center">
        <input
          type="text"
          placeholder="Search by title or author..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="border border-gray-300 p-2 rounded w-full max-w-md"
        />
        {!query && <p className="text-sm text-gray-500 mt-2">Showing 10 most recent papers by default.</p>}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {filteredPapers.map((p, idx) => (
          <div key={idx} className="bg-white shadow rounded-xl p-5 border border-gray-200 text-left">
            <h2 className="text-lg font-bold mb-1">{p.title}</h2>
            <p className="text-sm text-gray-600 mb-1">{p.authors}</p>
            <p className="text-xs text-gray-400 mb-2">{p.date}</p>
            <a href={p.link} className="text-blue-600 hover:underline" target="_blank" rel="noopener noreferrer">
              View Paper
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
