import React, { useEffect, useState } from "react";

export default function People() {
  const [people, setPeople] = useState([]);

  useEffect(() => {
    fetch("/seqamlab/data/people.json")
      .then((res) => res.json())
      .then((data) => setPeople(data));
  }, []);

  const current = people.filter((p) => p.status !== "past");
  const past = people.filter((p) => p.status === "past");

  const renderPerson = (person) => (
    <div key={person.name} className="border rounded p-4 shadow bg-white">
      <h3 className="text-lg font-semibold">{person.name}</h3>
      <p className="text-sm text-gray-600">{person.title}</p>
      <p className="text-sm mt-1">{person.research_interests}</p>
      {person.profile_link && (
        <a href={person.profile_link} target="_blank" rel="noreferrer" className="text-blue-600 text-sm mt-2 inline-block">
          Profile
        </a>
      )}
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto px-4 py-8 space-y-10">
      <div>
        <h2 className="text-2xl font-bold mb-4 text-[#cc0033]">Lab Members</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {current.map(renderPerson)}
        </div>
      </div>

      <div>
        <h2 className="text-2xl font-bold mt-10 mb-4 text-gray-800">Former Lab Members</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {past.map(renderPerson)}
        </div>
      </div>
    </div>
  );
}
