import React from "react";
import papers from "../data/papers.json";
import people from "../data/people.json";
import projects from "../data/projects.json";

export default function Home() {
  const featuredPapers = papers.slice(0, 3);
  const featuredPeople = people.slice(0, 3);
  const featuredProjects = projects.slice(0, 3);

  return (
    <div className="p-6 text-center">
      <h1 className="text-4xl font-bold text-[#cc0033] mb-4">
        Sequence Analysis and Modeling (SEQAM) Lab
      </h1>
      <p className="text-lg max-w-2xl mx-auto mb-8">
        Research at the intersection of sequence modeling, statistical inference, and machine learning at Rutgers University.
      </p>

      <section className="mb-10">
        <h2 className="text-2xl font-semibold text-[#cc0033] mb-2">Featured Papers</h2>
        <ul className="space-y-2">
          {featuredPapers.map((p, idx) => (
            <li key={idx} className="border p-4 rounded shadow text-left max-w-2xl mx-auto">
              <h3 className="font-bold">{p.title}</h3>
              <p className="text-sm text-gray-600">{p.authors} ({p.date})</p>
              <a href={p.link} className="text-blue-600" target="_blank" rel="noopener noreferrer">View Paper</a>
            </li>
          ))}
        </ul>
      </section>

      <section className="mb-10">
        <h2 className="text-2xl font-semibold text-[#cc0033] mb-2">Featured Projects</h2>
        <ul className="space-y-2">
          {featuredProjects.map((proj, idx) => (
            <li key={idx} className="border p-4 rounded shadow text-left max-w-2xl mx-auto">
              <h3 className="font-bold">{proj.title}</h3>
              <p className="text-sm text-gray-700">{proj.description}</p>
            </li>
          ))}
        </ul>
      </section>

      <section className="mb-10">
        <h2 className="text-2xl font-semibold text-[#cc0033] mb-2">Lab Members</h2>
        <ul className="space-y-2">
          {featuredPeople.map((person, idx) => (
            <li key={idx} className="border p-4 rounded shadow text-left max-w-2xl mx-auto">
              <h3 className="font-bold">{person.name}</h3>
              <p>{person.title}</p>
              <p className="text-sm text-gray-600">{person.research_interests.join(', ')}</p>
              <a href={person.profile_link} className="text-blue-600" target="_blank" rel="noreferrer">Profile</a>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
