import React, { useEffect, useState } from "react";

export default function Home() {
  const [papers, setPapers] = useState([]);
  const [projects, setProjects] = useState([]);
  const [courses, setCourses] = useState([]);
  const [people, setPeople] = useState([]);

  useEffect(() => {
    fetch("/seqamlab/data/papers.json").then((res) => res.json()).then(setPapers);
    fetch("/seqamlab/data/projects.json").then((res) => res.json()).then(setProjects);
    fetch("/seqamlab/data/courses.json").then((res) => res.json()).then(setCourses);
    fetch("/seqamlab/data/people.json").then((res) => res.json()).then(setPeople);
  }, []);

  const recent = (list, n = 5) =>
    [...list]
      .sort((a, b) => (b.year || b.date || "").localeCompare(a.year || a.date || ""))
      .slice(0, n);

  const currentPeople = people.filter(p => p.status !== "past").slice(0, 5);

  return (
    <div className="max-w-5xl mx-auto px-4 py-8 space-y-12">
      <header className="text-center">
        <h1 className="text-3xl sm:text-4xl font-bold text-[#cc0033] mb-4">
          Sequence Analysis and Modeling Lab (SEQAM)
        </h1>
      </header>

      <section>
        <h2 className="text-2xl font-bold text-[#cc0033] mb-4">Recent Papers</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {recent(papers).map((paper, i) => (
            <div key={i} className="border rounded p-4 shadow bg-white">
              <h3 className="font-semibold text-base">{paper.title}</h3>
              <p className="text-sm text-gray-700 mt-1">{paper.authors} ({paper.year})</p>
              {paper.journal && <p className="text-sm italic text-gray-600">{paper.journal}</p>}
              {paper.doi && <a href={`https://doi.org/${paper.doi}`} target="_blank" rel="noreferrer" className="text-sm text-blue-600 mt-2 inline-block">DOI</a>}
            </div>
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-2xl font-bold text-[#cc0033] mb-4">Featured Projects</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {recent(projects).map((project, i) => (
            <div key={i} className="border rounded p-4 shadow bg-white">
              <h3 className="font-semibold text-base">{project.title}</h3>
              <p className="text-sm mt-1 text-gray-700">{project.description}</p>
            </div>
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-2xl font-bold text-[#cc0033] mb-4">Courses</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {recent(courses).map((course, i) => (
            <div key={i} className="border rounded p-4 shadow bg-white">
              <h3 className="font-semibold text-base">{course.name}</h3>
              <p className="text-sm text-gray-700 mt-1">{course.semester}</p>
              <p className="text-sm mt-1">{course.description}</p>
            </div>
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-2xl font-bold text-[#cc0033] mb-4">Lab Members</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {currentPeople.map((person, i) => (
            <div key={i} className="border rounded p-4 shadow bg-white">
              <h3 className="font-semibold text-base">{person.name}</h3>
              <p className="text-sm text-gray-700">{person.title}</p>
              {person.profile_link && (
                <a href={person.profile_link} target="_blank" rel="noreferrer" className="text-blue-600 text-sm mt-2 inline-block">
                  Profile
                </a>
              )}
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
